import { Component, Input, OnInit } from '@angular/core';
import { CoherencyReportService } from 'src/app/services/coherency-report.service';
import { ActivatedRoute, Router } from '@angular/router';
import { CoherencyReport } from 'src/app/models/coherency-report.model';

@Component({
  selector: 'app-coherency-report-details',
  templateUrl: './coherency-report-details.component.html',
  styleUrls: ['./coherency-report-details.component.css']
})
export class CoherencyReportDetailsComponent implements OnInit {

  @Input() viewMode = false;

  @Input() currentCoherencyReport: CoherencyReport = {
      serZone: '',
      type: '',
      tag: '',
      description: '',
      comment: ''
    };

  message = '';

  constructor(
    private coherencyReportService: CoherencyReportService,
    private route: ActivatedRoute,
    private router: Router) { }

  ngOnInit(): void {
    if (!this.viewMode) {
      this.message = '';
      this.getCoherencyReportById(this.route.snapshot.params["id"]);
    }
  }

  getCoherencyReportById(id: number): void {
    this.coherencyReportService.getCoherencyReportById(id)
      .subscribe({
        next: (data) => {
          this.currentCoherencyReport = data;
          console.log(data);
        },
        error: (e) => console.error(e)
      });
  }

  updateCoherencyReport(): void {
    this.message = '';

    this.coherencyReportService.updateCoherencyReport(this.currentCoherencyReport.id!, this.currentCoherencyReport)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.message = res.message ? res.message : 'This tutorial was updated successfully!';
        },
        error: (e) => console.error(e)
      });
  }

  deleteCoherencyReport(): void {
    this.coherencyReportService.deleteCoherencyReport(this.currentCoherencyReport.id!)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.router.navigate(['/coherencyReports']);
        },
        error: (e) => console.error(e)
      });
  }
}
<div *ngIf="viewMode; else editable">
  <div *ngIf="currentCoherencyReport.id">
    <h4>Coherency Report</h4>
    <div>
      <label><strong>SerZone:</strong></label> {{ currentCoherencyReport.serZone }}
    </div>
    <div>
      <label><strong>Type:</strong></label> {{ currentCoherencyReport.type }}
    </div>
    <div>
      <label><strong>Tag:</strong></label> {{ currentCoherencyReport.tag }}
    </div>
    <div>
      <label><strong>Description:</strong></label>
      {{ currentCoherencyReport.description }}
    </div>
    <div>
      <label><strong>Comment:</strong></label>
      {{ currentCoherencyReport.comment }}
    </div>

    <a
      class="badge badge-warning"
      routerLink="/coherencyReports/{{ currentCoherencyReport.id }}"
    >
      Edit
    </a>
  </div>

  <div *ngIf="!currentCoherencyReport">
    <br />
    <p>Please click on a Coherency Report...</p>
  </div>
</div>

<ng-template #editable>
  <div *ngIf="currentCoherencyReport.id" class="edit-form">
    <h4>Coherency Report</h4>
    <form>
      <div class="form-group">
        <label for="serZone">SerZone</label>
        <input
          type="text"
          class="form-control"
          id="serZone"
          [(ngModel)]="currentCoherencyReport.serZone"
          name="serZone"
        />
      </div>
      <div class="form-group">
        <label for="type">Type</label>
        <input
          type="type"
          class="form-control"
          id="type"
          [(ngModel)]="currentCoherencyReport.type"
          name="type"
        />
      </div>
      <div class="form-group">
        <label for="tag">Tag</label>
        <input
          type="text"
          class="form-control"
          id="tag"
          [(ngModel)]="currentCoherencyReport.tag"
          name="tag"
        />
      </div>
      <div class="form-group">
        <label for="description">Description</label>
        <input
          type="text"
          class="form-control"
          id="description"
          [(ngModel)]="currentCoherencyReport.description"
          name="description"
        />
      </div>
      <div class="form-group">
        <label for="comment">Comment</label>
        <input
          type="text"
          class="form-control"
          id="comment"
          [(ngModel)]="currentCoherencyReport.comment"
          name="comment"
        />
      </div>
    </form>

    <button class="badge badge-danger mr-2" (click)="deleteCoherencyReport()">
      Delete
    </button>

    <button
      type="submit"
      class="badge badge-success mb-2"
      (click)="updateCoherencyReport()"
    >
      Update
    </button>
    <p>{{ message }}</p>
  </div>

  <div *ngIf="!currentCoherencyReport.id">
    <br />
    <p>Cannot access this Coherency Report...</p>
  </div>
</ng-template>
